package Week5;

public class ExceptionExplanation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,j, k= 0;
		
		i=5;
		j=0;
		if(j==0)
		{
			System.out.println("Invalid operation>");
		}
		else
		{
			k = i/j;
		}
		
		
		System.out.println("k-->"+k);

	}

}
